var textForPages =["\u8272\u5f69\u8207\u60c5\u7dd2\u63a2\u8a0e\u000d\u000a2O25\u000d\u000a\u63a2\u7d22\u7c89\u5f69\u8272\u7684\u5fc3\u9748\u529b\u91cf\u000d\u000a\u81ea\u000d\u000a\u6211\u000d\u000a\u7642\u000d\u000a\u7652\u000d\u000a\u548c\u000d\u000a\u8ae7\u000d\u000a\u7c89\u000d\u000a\u5f69\u000d\u000a\u300c\u900f\u904e\u548c\u8ae7\u7c89\u5f69\uff0c\u958b\u555f\u5fc3\u9748\u7684\u81ea\u6211\u5c0d\u8a71\u300d\u000d\u000aBY: \u5fe0\u5b8f\u000d\u000a\u8207\u81ea\u5df1\u548c\u89e3\u7684\u7e6a\u756b\u65c5\u7a0b\u000d\u000a\u300c\u000d\u000a\u300d","\u7c21\u4ecb\u000d\u000a\u60c5\u7dd2\u5982\u540c\u7121\u5f62\u7684\u98a8\uff0c\u800c\u8272\u5f69\u5177\u6709\u529b\u91cf\u7684\uff0c\u6bcf\u500b\u4eba\u90fd\u80fd\u5728\u767d\u7d19\u4e0a\u7e6a\u756b\u51fa\u000d\u000a\u4e86\u60c5\u7dd2\u7684\u8272\u5f69...... 3\u000d\u000a\u4eba\u7269\u4ecb\u7d39\u000d\u000a\u5c0f\u7de8--\u5fe0\u5b8f\u000d\u000a\u6a02\u9f61\u5925\u4f34--\u5927\u5b6b\u59ca\u000d\u000a4\u000d\u000a\u7c89\u5f69\u756b\u7684\u5c0f\u5287\u5834\u000d\u000a\u7c89\u5f69\u756b\u5982\u4f55\u8868\u9054\u60c5\u7dd2\uff1f\u000d\u000a\u9752\u9280\u5171\u5b78\u7c89\u5f69\u756b\u000d\u000a5\u000d\u000a\u60c5\u7dd2\u4e4b\u65c5\u000d\u000a\u5feb\u6a02\u5927\u4e0d\u540c\uff1f\uff01\u000d\u000a\u8207\u6a02\u9f61\u7684\u90a3\u4e9b\u4e8b\u000d\u000a6-7\u000d\u000a\u60c5\u7dd2\u5f88\u7c21\u55ae\u000d\u000a\u6182\u9b31\u5fc3\u000d\u000a\u61a4\u6012\u76f8\u000d\u000a\u6b61\u6a02\u5c0f\u9d28\u000d\u000a8-9\u000d\u000a\u60c5\u7dd2\u7684\u50f9\u503c\u000d\u000a\u6050\u61fcVS\u81ea\u7531\u000d\u000a\u7126\u616eVS\u4fe1\u4efb\u000d\u000a\u6cae\u55aaVS\u632f\u596e\u000d\u000a10-11\u000d\u000a\u756b\u756b\u7684\u5c0f\u6487\u6b65 12\u000d\u000a\u5982\u4f55\u7528\u8272\u5f69\u7e6a\u51fa\u60c5\u7dd2\uff1f 13\u000d\u000a\u76ee\u9304\u000d\u000a2","\u60c5\u7dd2\u5982\u540c\u7121\u5f62\u7684\u98a8\uff0c\u6642\u800c\u8f15\u67d4\u3001\u6642\u800c\u000d\u000a\u6d36\u6e67\uff0c\u6bcf\u500b\u4eba\u5728\u65e5\u5e38\u751f\u6d3b\u4e2d\uff0c\u90fd\u5728\u4e0d\u000d\u000a\u65b7\u7d93\u6b77\u8457\u5404\u7a2e\u60c5\u7dd2\u7684\u8d77\u4f0f\u3002\u800c\u8272\u5f69\uff0c\u000d\u000a\u6b63\u662f\u4e00\u7a2e\u6700\u76f4\u89ba\u3001\u6700\u6709\u529b\u91cf\u7684\u8a9e\u8a00\uff0c\u000d\u000a\u80fd\u5920\u5c07\u6211\u5011\u5167\u5fc3\u6df1\u8655\u7684\u60c5\u7dd2\u6084\u6084\u5730\u8868\u000d\u000a\u9054\u51fa\u4f86\u5728\u9752\u9280\u5171\u5b78\u7684\u7c89\u5f69\u756b\u6d3b\u52d5\u4e2d\uff0c\u000d\u000a\u8272\u5f69\u6210\u70ba\u4e86\u4e0d\u540c\u4e16\u4ee3\u4e4b\u9593\u6e9d\u901a\u7684\u6a4b\u000d\u000a\u6a11\u3002\u000d\u000a\u7c21\u4ecb\u000d\u000a\u5e74\u8f15\u4eba\u5e36\u8457\u6d3b\u529b\u8207\u5275\u610f\uff0c\u9577\u8005\u64c1\u6709\u8457\u8c50\u5bcc\u7684\u4eba\u751f\u6545\u4e8b\uff0c\u5169\u4ee3\u4eba\u900f\u000d\u000a\u904e\u624b\u4e2d\u7684\u7c89\u5f69\uff0c\u5c07\u81ea\u5df1\u7684\u60c5\u7dd2\u8207\u56de\u61b6\u6162\u6162\u92ea\u5c55\u5728\u756b\u7d19\u4e0a\u3002\u9019\u6a23\u7684\u000d\u000a\u5171\u5b78\uff0c\u4e0d\u53ea\u662f\u6280\u85dd\u7684\u4ea4\u6d41\uff0c\u66f4\u662f\u4e00\u5834\u5fc3\u9748\u7684\u5c0d\u8a71\u2014\u2014\u5f7c\u6b64\u5728\u8272\u5f69\u000d\u000a\u4e4b\u9593\u627e\u5230\u4e86\u5171\u9cf4\uff0c\u4e5f\u5728\u5275\u4f5c\u4e2d\u611f\u53d7\u5230\u88ab\u7406\u89e3\u8207\u63a5\u7d0d\u3002\u85c9\u7531\u7c89\u5f69\u7684\u000d\u000a\u6eab\u67d4\u8cea\u611f\uff0c\u6bcf\u4e00\u7b46\u8f15\u8f15\u62b9\u4e0b\uff0c\u4e0d\u50c5\u63cf\u7e6a\u4e86\u773c\u524d\u7684\u756b\u9762\uff0c\u66f4\u64ab\u6170\u4e86\u000d\u000a\u5167\u5fc3\u7684\u60c5\u7dd2\u6ce2\u52d5\u3002\u8272\u5f69\u6210\u70ba\u4e86\u60c5\u611f\u7684\u5ef6\u4f38\uff0c\u9752\u9280\u5171\u5b78\u5247\u8b93\u9019\u4efd\u5ef6\u000d\u000a\u4f38\u66f4\u52a0\u6df1\u523b\u800c\u52d5\u4eba\u3002\u000d\u000a3","\u4eba\u7269\u4ecb\u7d39\u000d\u000a\u6a02\u9f61\u4eba\u58eb - \u5927\u5b6b\u59ca\u000d\u000a\u5c0f\u7de8 - \u5fe0\u5b8f\u000d\u000a\u5c0f\u7de8\u4f86\u81ea\u99ac\u4f86\u897f\u4e9e\uff0c\u76ee\u524d\u5c31\u8b80\u570b\u7acb\u81fa\u000d\u000a\u7063\u79d1\u6280\u5927\u5b78\u7684\u4f01\u696d\u7ba1\u7406\u7cfb\u3002\u5728\u9019\u6b21\u7684\u000d\u000a\u8ab2\u7a0b\u4e2d\u975e\u5e38\u9ad8\u8208\uff0c\u6536\u7a6b\u4e86\u5f88\u591a\u7684\u65b0\u9ad4\u000d\u000a\u9a57\u548c\u5b78\u7fd2\u5230\u4e86\u65b0\u6280\u6cd5\u3002\u672c\u4eba\u975e\u5e38\u69ae\u5e78\u000d\u000a\u6709\u9019\u500b\u6a5f\u6703\u8a8d\u8b58\u4e86\u9019\u6b21\u8ab2\u7a0b\u7684\u5925\u4f34--\u5927\u000d\u000a\u5b6b\u59ca\u3002\u9019\u5802\u8ab2\u8b93\u6211\u6700\u5feb\u6a02\u7684\u662f\u80fd\u5920\u64c1\u000d\u000a\u6709\u4e00\u500b\u975c\u4e0b\u5fc3\u7684\u6642\u9593\u53bb\u756b\u756b\u3002\u6211\u672c\u4eba\u000d\u000a\u5c0d\u65bc\u756b\u756b\u9019\u65b9\u9762\u5f88\u5c11\u6d89\u53ca\uff0c\u4f46\u662f\u5728\u9019\u000d\u000a\u6b21\u7684\u8ab2\u7a0b\u4e2d\u611f\u89ba\u756b\u756b\u7684\u6642\u9593\u904e\u5f97\u975e\u5e38\u000d\u000a\u5feb\uff0c\u4e5f\u8b93\u6211\u559c\u6b61\u4e0a\u4e86\u9019\u500b\u6d3b\u52d5\u3002\u000d\u000a\u5927\u5b6b\u59ca\u662f\u6211\u5728\u9019\u500b\u8ab2\u7a0b\u7684\u5925\u4f34\u3002\u5979\u662f\u4e00\u500b\u000d\u000a\u975e\u5e38\u6a02\u89c0\u7684\u4eba\u3002\u5728\u9019\u6b21\u8ab2\u7a0b\u4e2d\u6b63\u662f\u56e0\u70ba\u6709\u000d\u000a\u5979\u624d\u8b93\u6211\u89ba\u5f97\u975e\u5e38\u6709\u8da3\u3002\u5728\u756b\u756b\u4ee5\u53ca\u60c5\u7dd2\u000d\u000a\u4e0a\u6709\u8457\u81ea\u5df1\u7368\u7279\u7684\u898b\u89e3\u3002\u5979\u66fe\u63d0\u5230\u5979\u505a\u4eba\u000d\u000a\u7684\u5c0f\u9053\u7406\u201c\u4ee5\u7d42\u70ba\u59cb\u201d\u3002\u8981\u7372\u5f97\u81ea\u7531\u4e4b\u524d\u000d\u000a\u5fc5\u9808\u5b78\u6703\u81ea\u5f8b\u3002\u51e1\u4e8b\u201c\u4ee5\u7d42\u70ba\u59cb\u201d\uff0c\u5148\u8a2d\u000d\u000a\u5b9a\u597d\u76ee\u6a19\uff0c\u5805\u5b9a\u800c\u52aa\u529b\u7684\u524d\u9032\uff0c\u7d42\u80fd\u9054\u6210\u000d\u000a\u76ee\u6a19\u3002\u8981\u4fdd\u6301\u6a02\u89c0\u5584\u826f\u7684\u5fc3\uff0c\u5fc5\u5c07\u6210\u70ba\u7121\u000d\u000a\u6240\u754f\u61fc\u7684\u4eba\u3002\u5979\u4e0d\u77e5\u9053\u6050\u61fc\u662f\u4ec0\u9ebc\u3002\u4f46\u5979\u000d\u000a\u77e5\u9053\u81ea\u7531\u50cf\u662f\u98db\u5728\u5929\u7a7a\u7684\u559c\u9d72\u4e00\u6a23\u81ea\u7531\u98db\u000d\u000a\u7fd4\u3002\u000d\u000a4","\u7c89\u5f69\u756b\u7684\u5c0f\u5287\u5834\u000d\u000a2025\u000d\u000a\u7c89\u5f69\u756b\u5982\u4f55\u8868\u9054\u60c5\u7dd2\uff1f\u000d\u000a\u4ee5\u7c89\u5f69\u756b\u4f86\u7e6a\u51fa\u81ea\u5df1\u5728\u6bcf\u500b\u60c5\u7dd2\u6240\u7559\u4e0b\u000d\u000a\u7684\u984f\u8272\u3002\u6bcf\u7a2e\u60c5\u7dd2\u90fd\u6709\u5176\u8c61\u5fb5\u7684\u984f\u8272\u3002\u000d\u000a\u7c89\u5f69\u756b\u56e0\u5176\u67d4\u548c\u7684\u8cea\u5730\u548c\u53ef\u6df7\u8272\u7684\u7279\u6027\u000d\u000a\u6210\u70ba\u8868\u9054\u5167\u5fc3\u60c5\u7dd2\u7684\u7406\u60f3\u5a92\u6750\u3002\u4f8b\u5982\uff1a\u000d\u000a\u8272\u8abf\u3001\u9ad8\u5c0d\u6bd4\uff0c\u67d4\u548c\u8272\u5f69\u80fd\u5920\u5b8c\u7f8e\u8868\u73fe\u000d\u000a\u51fa\u5167\u5fc3\u8c50\u5bcc\u7684\u60c5\u7dd2\u3002\u000d\u000a\u8272\u5f69\u6210\u70ba\u4e86\u60c5\u7dd2\u7684\u8a9e\u8a00\uff0c\u4e5f\u662f\u4e16\u4ee3\u6e9d\u000d\u000a\u901a\u7684\u6a4b\u6a11\u3002\u9752\u9280\u900f\u904e\u8272\u5f69\u548c\u5275\u4f5c\uff0c\u5c55\u000d\u000a\u958b\u4e86\u4e00\u5834\u6df1\u523b\u7684\u5fc3\u9748\u4ea4\u6d41\u3002\u7c89\u5f69\u6eab\u67d4\u000d\u000a\u7684\u8cea\u5730\uff0c\u8b93\u6bcf\u4e00\u7b46\u5283\u4e0b\u90fd\u50cf\u662f\u60c5\u611f\u7684\u000d\u000a\u5ef6\u4f38\uff0c\u7121\u8ad6\u662f\u559c\u6085\u3001\u7126\u616e\u3001\u4fe1\u4efb\u3001\u5b64\u000d\u000a\u5bc2\u9084\u662f\u81ea\u7531\uff0c\u90fd\u80fd\u81ea\u7136\u5730\u5728\u756b\u7d19\u4e0a\u6d41\u000d\u000a\u52d5\u8207\u5448\u73fe\u3002\u9019\u5834\u5171\u5b78\uff0c\u4e0d\u53ea\u662f\u6280\u85dd\u7684\u000d\u000a\u4ea4\u6d41\uff0c\u66f4\u662f\u4e00\u6b21\u8de8\u8d8a\u5e74\u9f61\u754c\u9650\u7684\u5fc3\u9748\u000d\u000a\u64c1\u62b1\uff0c\u8b93\u60c5\u611f\u5728\u7c89\u5f69\u4e2d\u6084\u6084\u6d41\u8f49\u3001\u7dbb\u000d\u000a\u653e\u3002\u000d\u000a\u9752\u9280\u5171\u5b78\u7c89\u5f69\u756b\u000d\u000a5","\u5feb\u6a02\u5c0d\u6bcf\u500b\u4eba\u4f86\u8aaa\uff0c\u90fd\u6709\u8457\u7368\u7279\u4e14\u73cd\u8cb4\u7684\u6a21\u000d\u000a\u6a23\u3002\u5feb\u6a02\u662f\u5f97\u5230\u79ae\u7269\uff1f\u7372\u5f97\u7a31\u8b9a\uff1f\u966a\u4f34\u9084\u662f\u000d\u000a\u4e00\u500b\u64c1\u62b1\uff1f\u6b64\u6b21\u8207\u6a02\u9f61\u4eba\u58eb\u63a2\u8a0e\u51fa\u7684\u5feb\u6a02\uff0c\u000d\u000a\u767c\u73fe\u6211\u5011\u5b9a\u7fa9\u7684\u5feb\u6a02\u4e0d\u540c\u3002\u5728\u8207\u6a02\u9f61\u4eba\u58eb\u7684\u000d\u000a\u4ea4\u6d41\u4e2d\uff0c\u6211\u807d\u898b\u4e86\u4e0d\u540c\u751f\u547d\u968e\u6bb5\u5c0d\u5feb\u6a02\u7684\u7406\u000d\u000a\u89e3\uff1a\u6709\u4e9b\u4eba\u89ba\u5f97\uff0c\u4e00\u53e5\u771f\u5fc3\u7684\u95dc\u5fc3\u52dd\u904e\u5343\u8a00\u000d\u000a\u842c\u8a9e\uff1b\u6709\u4e9b\u4eba\u8a8d\u70ba\uff0c\u8207\u5bb6\u4eba\u5171\u5ea6\u5e73\u51e1\u7684\u4e00\u000d\u000a\u5929\uff0c\u5c31\u662f\u6700\u5927\u7684\u5e78\u798f\u3002\u4e5f\u6709\u4eba\u5206\u4eab\uff0c\u80fd\u5920\u5065\u000d\u000a\u5eb7\u5730\u8fce\u63a5\u6bcf\u500b\u6e05\u6668\uff0c\u5c31\u662f\u6700\u503c\u5f97\u611f\u6069\u7684\u5feb\u000d\u000a\u6a02\u3002\u900f\u904e\u9019\u6b21\u7684\u5c0d\u8a71\uff0c\u6211\u5011\u767c\u73fe\u5feb\u6a02\u4e26\u7121\u7d55\u000d\u000a\u5c0d\u6a19\u6e96\uff0c\u5b83\u4e0d\u5728\u65bc\u5f62\u5f0f\u7684\u5927\u5c0f\uff0c\u800c\u5728\u65bc\u5fc3\u9748\u000d\u000a\u7684\u89f8\u52d5\u8207\u6eab\u6696\u7684\u9023\u7d50\u3002\u000d\u000a\u5176\u5be6\u5728\u6bcf\u4e00\u6b21\u8207\u6211\u7684\u6a02\u9f61\u5925\u4f34\u2014\u2014\u5927\u5b6b\u59ca\u4e00\u000d\u000a\u8d77\u756b\u756b\u5c31\u7b97\u662f\u4e00\u4ef6\u958b\u5fc3\u7684\u4e8b\u4e86\u3002\u6211\u66fe\u554f\u904e\u5927\u000d\u000a\u5b6b\u59ca\uff1a\u201c\u5927\u5b6b\u59ca\uff0c\u5176\u5be6\u70ba\u4ec0\u9ebc\u6703\u60f3\u4f86\u4e0a\u8ab2\u000d\u000a\u5462\uff1f\u70ba\u4ec0\u9ebc\u6703\u9078\u64c7\u4f86\u53c3\u52a0\u9752\u9280\u5171\u5b78\u5462\uff1f\u201d\u800c\u000d\u000a\u5927\u5b6b\u59ca\u56de\u7b54\u7684\u662f\uff1a\u201c\u5176\u5be6\u6211\u4f86\u8207\u4f60\u5011\u5171\u5b78\u662f\u000d\u000a\u60f3\u8981\u6311\u6230\u81ea\u5df1\uff0c\u6211\u8eab\u908a\u7684\u4eba\u90fd\u8aaa\u8207\u5c0f\u5b69\u4ea4\u6d41\u000d\u000a\u975e\u5e38\u8271\u96e3\uff0c\u4f46\u662f\u6211\u5c31\u662f\u559c\u6b61\u6311\u6230\u81ea\u5df1\uff0c\u800c\u4e14\u000d\u000a\u8207\u4f60\u5011\u4e00\u8d77\u5c31\u975e\u5e38\u5feb\u6a02\u201d\u000d\u000a\u3002\u9019\u6642\uff0c\u6211\u4e86\u89e3\u5230\u000d\u000a\u4e86\u5927\u5b6b\u59ca\u662f\u4e00\u500b\u975e\u5e38\u6a02\u89c0\u7684\u4eba\uff0c\u7121\u8ad6\u4ec0\u9ebc\u5e74\u000d\u000a\u9f61\u90fd\u5728\u52c7\u65bc\u6311\u6230\u81ea\u5df1\u3002\u5728\u756b\u7c89\u5f69\u756b\u7684\u6642\u5019\u4e5f\u000d\u000a\u662f\u5979\u7684\u7b11\u8072\u6253\u7834\u4e86\u51b7\u51b7\u7684\u6c23\u6c1b\uff0c\u5e36\u4f86\u6eab\u6696\u7684\u000d\u000a\u6c1b\u570d\u3002\u000d\u000a\u8207\u6a02\u9f61\u7684\u90a3\u4e9b\u4e8b\u000d\u000a\u60c5\u7dd2\u4e4b\u65c5\u000d\u000a\u5feb\u6a02\u5927\u4e0d\u540c\uff1f\uff01\u000d\u000a6","\u9996\u5148\uff0c\u6216\u8a31\u662f\u6210\u9577\u80cc\u666f\u4e0d\u540c\u3002\u56e0\u70ba\u6bcf\u4e00\u500b\u4e16\u4ee3\u6210\u9577\u7684\u6642\u4ee3\u80cc\u666f\u3001\u793e\u6703\u000d\u000a\u74b0\u5883\u548c\u50f9\u503c\u89c0\u90fd\u4e0d\u540c\u3002\u4f8b\u5982\uff1a\u6a02\u9f61\u4e16\u4ee3\u7d93\u6b77\u904e\u7269\u8cc7\u6bd4\u8f03\u5331\u4e4f\u3001\u793e\u6703\u8b1b\u000d\u000a\u6c42\u5fcd\u8010\u8207\u52aa\u529b\u7684\u5e74\u4ee3\uff0c\u4ed6\u5011\u6bd4\u8f03\u7fd2\u6163\u58d3\u6291\u60c5\u7dd2\u3001\u91cd\u8996\u96c6\u9ad4\u548c\u5bb6\u5ead\u3002\u800c\u000d\u000a\u5e74\u8f15\u4e16\u4ee3\u5247\u6210\u9577\u5728\u7269\u8cea\u8f03\u5145\u88d5\u3001\u9f13\u52f5\u81ea\u6211\u8868\u9054\u7684\u74b0\u5883\uff0c\u5c0d\u60c5\u7dd2\u7684\u8a8d\u8b58\u000d\u000a\u548c\u8868\u9054\u6bd4\u8f03\u76f4\u63a5\u3001\u81ea\u7531\u3002\u53e6\u5916\uff0c\u50f9\u503c\u89c0\u8207\u671f\u5f85\u4e0d\u540c\u3002\u4e0d\u540c\u4e16\u4ee3\u5c0d\u300c\u5feb\u000d\u000a\u6a02\u300d\u3001\u300c\u6210\u529f\u300d\u3001\u300c\u6eff\u8db3\u300d\u7684\u5b9a\u7fa9\u4e5f\u4e0d\u540c\u3002\u4f8b\u5982\uff1a\u6a02\u9f61\u4e16\u4ee3\u53ef\u80fd\u8a8d\u70ba\u000d\u000a\u7a69\u5b9a\u3001\u6709\u4eba\u966a\u4f34\u5c31\u662f\u5e78\u798f\u3002\u800c\u5e74\u8f15\u4eba\u53ef\u80fd\u8a8d\u70ba\u8981\u5be6\u73fe\u81ea\u6211\u3001\u6d3b\u51fa\u500b\u4eba\u000d\u000a\u7279\u8272\u624d\u662f\u771f\u6b63\u7684\u5feb\u6a02\u3002\u56e0\u6b64\u5728\u540c\u6a23\u4e00\u500b\u756b\u756b\u6d3b\u52d5\u4e2d\uff0c\u4e0d\u540c\u4e16\u4ee3\u7684\u4eba\u53ef\u000d\u000a\u80fd\u611f\u53d7\u5230\u7684\u5feb\u6a02\u4f86\u6e90\u548c\u60c5\u7dd2\u4e5f\u4e0d\u4e00\u6a23\u3002\u000d\u000a\u8207\u6a02\u9f61\u5171\u756b\u000d\u000a\u5728\u4e00\u500b\u6708\u7684\u6642\u9593\u975e\u5e38\u77ed\u66ab\u3002\u4f46\u000d\u000a\u662f\uff0c\u9019\u4e00\u500b\u6708\u6211\u767c\u73fe\u5230\u4e16\u4ee3\u7684\u4e0d\u000d\u000a\u540c\u4e5f\u9593\u63a5\u5f71\u97ff\u4e86\u60c5\u7dd2\u7684\u4e0d\u540c\uff0c\u9019\u000d\u000a\u6a23\u4e5f\u5c0e\u81f4\u4e86\u6bcf\u500b\u4eba\u7684\u756b\u90fd\u6709\u4e0d\u540c\u000d\u000a\u7684\u770b\u6cd5\u3002\u000d\u000a\u4e16\u4ee3\u7684\u4e0d\u540c\u70ba\u4f55\u8b93\u60c5\u7dd2\u8868\u9054\u548c\u60c5\u7dd2\u611f\u53d7\u6703\u6709\u5dee\u7570\uff1f\u000d\u000a7","\u60c5\u7dd2\u5f88\u7c21\u55ae\u000d\u000a\u60c5\u7dd2\u662f\u4e00\u500b\u5f88\u7279\u5225\u4e14\u57fa\u672c\u7684\u201c\u6771\u897f\u201d\u000d\u000a\u3002\u5728\u9019\u500b\u4e16\u754c\u4e0a\u7684\u6bcf\u500b\u4eba\u000d\u000a\u751a\u81f3\u52d5\u7269\u90fd\u6709\u60c5\u7dd2\u3002\u6bd4\u5982\uff1a\u5feb\u6a02\u3001\u61a4\u6012\u3001\u60b2\u50b7\u3001\u9a5a\u8a1d\u548c\u53ad\u60e1\u3002\u000d\u000a\u60c5\u7dd2\u5f71\u97ff\u8457\u4eba\u7684\u884c\u70ba\u548c\u6c7a\u7b56\u3002\u9069\u7576\u5730\u7ba1\u7406\u548c\u8868\u9054\u60c5\u7dd2\u6709\u52a9\u65bc\u5fc3\u000d\u000a\u7406\u5065\u5eb7\u3002\u60c5\u7dd2\u9084\u6703\u5f71\u97ff\u4eba\u7684\u8a18\u61b6\u3001\u5275\u9020\u529b\u548c\u5b78\u7fd2\u80fd\u529b\uff0c\u56e0\u6b64\u5c0d\u000d\u000a\u500b\u4eba\u767c\u5c55\u548c\u4eba\u969b\u95dc\u4fc2\u90fd\u5177\u6709\u91cd\u8981\u4f5c\u7528\u3002\u9069\u7576\u5730\u8abf\u7bc0\u60c5\u7dd2\u53ef\u4ee5\u63d0\u000d\u000a\u9ad8\u751f\u6d3b\u8cea\u91cf\uff0c\u4f7f\u4eba\u66f4\u597d\u5730\u61c9\u5c0d\u58d3\u529b\u548c\u6311\u6230\u3002\u000d\u000a\u6182\u9b31\u5fc3\u000d\u000a\u6b64\u756b\u4e2d\u592e\u4ee5\u7d2b\u7d05\u4ea4\u7e54\u7684\u5927\u5fc3\u5f62\u70ba\u4e3b\u9ad4\uff0c\u56db\u5468\u4f34\u96a8\u8457\u000d\u000a\u5e7e\u500b\u8f03\u5c0f\u3001\u8272\u8abf\u67d4\u548c\u7684\u5fc3\u5f62\u5716\u6848\u3002\u80cc\u666f\u4ee5\u7070\u8910\u8272\u70ba\u000d\u000a\u4e3b\u8272\u8abf\uff0c\u7b46\u89f8\u5448\u73fe\u6a21\u7cca\u3001\u7c89\u9727\u822c\u7684\u8cea\u611f\uff0c\u4f7f\u6574\u9ad4\u756b\u000d\u000a\u9762\u7030\u6f2b\u8457\u4e00\u7a2e\u4f4e\u6c89\u3001\u8ff7\u6fdb\u7684\u6c1b\u570d\u3002\u7d2b\u8272\u5e38\u8207\u5b64\u5bc2\u3001\u000d\u000a\u795e \u79d8 \u53ca \u6182 \u9b31 \u806f \u7e6b \u5728 \u4e00 \u8d77 \uff0c \u800c \u672c \u4f5c \u4e2d \u4e3b \u5fc3 \u5f62 \u6697 \u5e36 \u7d2b\u000d\u000a\u610f\uff0c\u8c61\u5fb5\u4e86\u5167\u5fc3\u60c5\u611f\u7684\u58d3\u6291\u8207\u6c89\u91cd\u3002\u642d\u914d\u7070\u6697\u7684\u80cc\u000d\u000a\u666f\u8207\u67d4\u5f31\u7684\u5468\u908a\u8272\u5f69\uff08\u5982\u7070\u7da0\u3001\u6de1\u7d05\uff09\uff0c\u66f4\u5f37\u5316\u4e86\u000d\u000a\u756b\u9762\u4e2d\u6182\u9b31\u60c5\u7dd2\u7684\u8868\u9054\uff0c\u80fd\u611f\u53d7\u5230\u5fc3\u9748\u88ab\u4e00\u5c64\u7121\u5f62\u000d\u000a\u8584\u9727\u7c60\u7f69\u7684\u611f\u53d7\u3002\u000d\u000a8","\u6b61\u6a02\u5c0f\u9d28\u000d\u000a\u5feb\u6a02\u662f\u4ec0\u9ebc\u5462\uff1f\u5feb\u6a02\u6216\u8a31\u662f\u5927\u81ea\u7136\u000d\u000a\u7684\u8774\u8776\u5728\u5feb\u6a02\u5730\u821e\u8e48\uff0c\u9084\u662f\u6e56\u4e0a\u7684\u000d\u000a\u9ec3\u8272\u5c0f\u9d28\u5feb\u6a02\u5730\u6e38\u6c34\u3002\u80cc\u666f\u4ee5\u6a58\u8272\u000d\u000a\u548c\u9ec3\u8272\u4f86\u71df\u9020\u51fa\u9ec3\u660f\u7684\u666f\u8272\u3002\u5e95\u90e8\u000d\u000a\u5247\u4ee5\u6dfa\u85cd\u8272\u4f86\u756b\u51fa\u6e56\u9762\u3002\u4ee5\u53e6\u4e00\u5f35\u000d\u000a\u81ea\u88fd\u7684\u5c0f\u9d28\u7d19\u6a21\u677f\u4f86\u756b\u51fa\u9019\u96bb\u5c0f\u000d\u000a\u9d28\u3002\u756b\u5b8c\u9ec3\u8272\u5c0f\u9d28\u4e4b\u5f8c\uff0c\u80cc\u666f\u7528\u7684\u000d\u000a\u6a59\u8272\u8207\u9ec3\u8272\u4ee3\u8868\u8457\u508d\u665a\u7684\u5929\u7a7a\u3002\u4ee5\u000d\u000a\u53ca\u7528\u85cd\u8272\u5728\u5c0f\u9d28\u4e0b\u756b\u4e0a\u6e56\u6cca\u3002\u53ef\u4ee5\u000d\u000a\u4f7f\u7528\u68b3\u5b50\u628a\u6e56\u6cca\u7684\u6c34\u7d0b\u63d0\u73fe\u51fa\u4f86\u8b93\u000d\u000a\u756b\u66f4\u52a0\u751f\u52d5\u3002\u000d\u000a\u61a4\u6012\u76f8\u000d\u000a\u61a4\u6012\u7684\u5a01\u529b\u975e\u5e38\u53ef\u6015\u3002\u9019\u5e45\u62bd\u8c61\u756b\u4ee5\u6df1\u000d\u000a\u8272\u7cfb\u8868\u9054\u61a4\u6012\u7684\u5f37\u70c8\u60c5\u611f\u3002\u8d77\u521d\uff0c\u7070\u8272\u000d\u000a\u8c61\u5fb5\u7070\u5fc3\uff0c\u96a8\u5f8c\u8f49\u70ba\u6697\u8272\u52a0\u6df1\u60c5\u7dd2\u3002\u7d05\u000d\u000a\u8272\u4ee3\u8868\u8b66\u544a\u8207\u5371\u96aa\uff0c\u800c\u9ed1\u8272\u5247\u5c55\u73fe\u61a4\u6012\u000d\u000a\u7684\u6975\u81f4\u8207\u7406\u667a\u7684\u5931\u63a7\u3002\u6700\u5f8c\uff0c\u85cd\u8272\u8986\u65bc\u000d\u000a\u9ed1\u8272\u4e4b\u4e0a\uff0c\u8c61\u5fb5\u51b7\u975c\u8207\u6062\u5fa9\u6e05\u660e\u3002\u6495\u4e0b\u000d\u000a\u81a0\u5e36\u4e0a\u7684\u661f\u661f\u6642\uff0c\u60c5\u7dd2\u96a8\u4e4b\u91cb\u653e\uff0c\u5f77\u5f7f\u000d\u000a\u61a4\u6012\u7834\u788e\uff0c\u56de\u6b78\u5e73\u975c\u3002\u5275\u4f5c\u904e\u7a0b\u8b93\u60c5\u7dd2\u000d\u000a\u5f97\u5230\u5ba3\u6d29\uff0c\u6295\u5165\u5176\u4e2d\u5f8c\uff0c\u518d\u6b21\u610f\u8b58\u5230\u9019\u000d\u000a\u53ea\u662f\u4e00\u5e45\u756b\uff0c\u61a4\u6012\u7d42\u5c07\u6d88\u6563\u3002\u000d\u000a9","\u9a5a\u6050\u7684\u5c0f\u9b3c\u000d\u000a\u6697\u8272\u7cfb\u88ab\u8c61\u5fb5\u8457\u4e00\u500b\u4eba\u7684\u8ca0\u80fd\u91cf\u3002\u6b64\u6b21\u7684\u9ed1\u8272\u8c61\u5fb5\u000d\u000a\u672a\u77e5\u8207\u4e0d\u5b89\uff0c\u6697\u7d05\u8272\u50cf\u4e00\u6839\u4e00\u6839\u7684\u7d05\u8272\u9396\u93c8\u4ee3\u8868\u58d3\u000d\u000a\u529b\u8207\u5371\u6a5f\u611f\uff0c\u6574\u9ad4\u756b\u9762\u7dca\u7e43\u58d3\u8feb\uff0c\u50b3\u9054\u51fa\u88ab\u6050\u61fc\u56f0\u000d\u000a\u4f4f\u3001\u7121\u6cd5\u547c\u5438\u7684\u611f\u53d7\u3002\u5c0f\u9b3c\u7684\u8868\u60c5\u9a5a\u6050\uff0c\u9032\u4e00\u6b65\u5f37\u000d\u000a\u5316\u4e86\u5167\u5fc3\u5bb3\u6015\u8207\u5b64\u7acb\u7684\u60c5\u7dd2\u3002\u000d\u000a\u60a0\u6e38\u7684\u9be8\u9b5a\u8207\u6c34\u6bcd\u000d\u000a\u5728\u6211\u7684\u5370\u8c61\u4e2d\u85cd\u8272\u4ee3\u8868\u5ee3\u95ca\u3001\u5e73\u975c\u8207\u81ea\u7531\uff0c\u5982\u540c\u5728\u000d\u000a\u907c\u95ca\u7684\u6d77\u6d0b\u4e2d\u81ea\u5728\u9068\u904a\u3002\u5728\u6211\u7684\u5370\u8c61\u4e2d\u9be8\u9b5a\u8c61\u5fb5\u8457\u000d\u000a\u5728\u6d77\u88e1\u7684\u975e\u5e38\u81ea\u7531\uff0c\u5b89\u5be7\u7684\u8c61\u5fb5\u3002\u800c\u6c34\u6bcd\u8f15\u76c8\u6f02\u000d\u000a\u6d6e\uff0c\u6697\u793a\u8457\u7121\u62d8\u7121\u675f\u3001\u96a8\u5fc3\u6240\u6b32\u7684\u81ea\u7531\u5fc3\u5883\u3002\u756b\u9762\u000d\u000a\u4e2d\u5411\u56db\u65b9\u767c\u6563\u7684\u767d\u8272\u7dda\u689d\uff0c\u66f4\u52a0\u5f37\u4e86\u958b\u653e\u8207\u8f15\u76c8\u7684\u000d\u000a\u611f\u89ba\u3002\u000d\u000a\u6050\u61fcVS\u81ea\u7531\u000d\u000a\u7126\u616eVS\u4fe1\u4efb\u000d\u000a\u7070\u8272\u8207\u6de1\u7da0\u8272\u4ea4\u7e54\u3002\u7070\u8272\u8c61\u5fb5\u8457\u8ff7\u60d8\u3001\u58d3\u6291\u8207\u4e0d\u000d\u000a\u78ba\u5b9a\uff0c\u8b93\u4eba\u611f\u5230\u6c89\u91cd\u8207\u7121\u529b\uff1b\u800c\u6de1\u7da0\u8272\u539f\u672c\u5e36\u6709\u000d\u000a\u5e73\u975c\u7684\u610f\u5473\uff0c\u5728\u7070\u8272\u7684\u58d3\u8feb\u4e0b\u537b\u986f\u5f97\u84bc\u767d\u7121\u529b\uff0c\u000d\u000a\u53cd\u6620\u51fa\u5167\u5fc3\u7684\u4e0d\u5b89\u8207\u6399\u624e\u3002\u7dda\u689d\u7684\u788e\u88c2\u611f\u8207\u756b\u9762\u000d\u000a\u4e2d\u7684\u6a21\u7cca\u904e\u6e21\uff0c\u8868\u73fe\u51fa\u7126\u616e\u6642\u601d\u7dd2\u7d1b\u4e82\u3001\u65b9\u5411\u4e0d\u000d\u000a\u660e\u7684\u5fc3\u7406\u72c0\u614b\u3002\u000d\u000a\u7070\u8272\u7684\u754c\u7dda\u000d\u000a\u7099\u71b1\u7684\u592a\u967d\u000d\u000a\u6eab\u6696\u7684\u9ec3\u8272\u5305\u88f9\u5728\u67d4\u548c\u7684\u7c89\u7d05\u8272\u4e2d\uff0c\u9ec3\u8272\u4ee3\u8868\u8457\u6eab\u000d\u000a\u6696\u3001\u5e0c\u671b\u8207\u6b63\u5411\u80fd\u91cf\uff0c\u7c89\u7d05\u8272\u5247\u8c61\u5fb5\u8457\u95dc\u611b\u8207\u5305\u000d\u000a\u5bb9\u3002\u5169\u7a2e\u8272\u5f69\u76f8\u878d\uff0c\u5f62\u6210\u67d4\u548c\u4e14\u6709\u529b\u91cf\u7684\u6c1b\u570d\uff0c\u50cf\u000d\u000a\u662f\u4e00\u7a2e\u6eab\u6696\u7684\u64c1\u62b1\uff0c\u5e36\u4f86\u5b89\u5168\u611f\u8207\u5b89\u5fc3\u3002\u4fe1\u4efb\u4f7f\u4eba\u000d\u000a\u5fc3\u4e2d\u5145\u6eff\u5149\u4eae\uff0c\u5373\u4f7f\u5916\u754c\u74b0\u5883\u8b8a\u5316\uff0c\u4e5f\u80fd\u4fdd\u6301\u5167\u5fc3\u000d\u000a\u7684\u7a69\u5b9a\u8207\u5f9e\u5bb9\u3002\u000d\u000a\u60c5\u7dd2\u7684\u50f9\u503c\u000d\u000a10","\u6cae\u55aaVS\u632f\u596e\u000d\u000a\u9019\u6b21\u7684\u6cae\u55aa\u756b\u4e3b\u8272\u662f\u6df1\u85cd\u504f\u9ed1\uff0c\u642d\u914d\u4e9b\u8a31\u000d\u000a\u7c89\u7d2b\u8272\u7684\u96ea\u82b1\u3002\u6574\u9ad4\u8272\u8abf\u51b7\u6697\u3002\u6df1\u85cd\u3001\u9ed1\u000d\u000a\u8272\u5e38\u8207\u60b2\u50b7\u3001\u58d3\u6291\u3001\u5b64\u55ae\u9023\u7d50\u3002\u98c4\u843d\u7684\u96ea\u000d\u000a\u82b1\u8c61\u5fb5\u5bd2\u51b7\u7684\u610f\u5883\uff0c\u6389\u843d\u7684\u5c0f\u788e\u7247\u4e5f\u8b93\u756b\u000d\u000a\u9762\u6709\u7834\u788e\u3001\u6d88\u901d\u7684\u611f\u89ba\uff0c\u547c\u61c9\u300c\u6cae\u55aa\u300d\u7684\u000d\u000a\u60c5\u7dd2\u3002\u6574\u9ad4\u7d66\u4eba\u975c\u6b62\u3001\u5b64\u5bc2\u7684\u611f\u53d7\u3002\u000d\u000a\u632f\u596e\u756b\u4e3b\u8272\u662f\u9bae\u660e\u7684\u7da0\u8272\uff0c\u642d\u914d\u7c89\u767d\u8272\u7684\u000d\u000a\u8c93\u3001\u8449\u5b50\u8207\u661f\u661f\u3002\u6574\u9ad4\u8272\u8abf\u660e\u4eae\u6eab\u6696\u3002\u7da0\u000d\u000a\u8272\u4ee3\u8868\u751f\u547d\u529b\u3001\u5e0c\u671b\u8207\u6210\u9577\u3002\u661f\u661f\u8c61\u5fb5\u5922\u000d\u000a\u60f3\u8207\u5e0c\u671b\uff0c\u8c93\u5247\u7d66\u4eba\u6eab\u6696\u8207\u966a\u4f34\u7684\u611f\u89ba\u3002\u000d\u000a\u6574\u5e45\u756b\u52d5\u614b\u611f\u5f37\uff0c\u6709\u4e00\u7a2e\u8fce\u98a8\u800c\u8d77\u3001\u5145\u6eff\u000d\u000a\u6d3b\u529b\u7684\u6c1b\u570d\uff0c\u975e\u5e38\u7b26\u5408\u300c\u632f\u596e\u300d\u7684\u4e3b\u984c\u3002\u000d\u000a\u98c4\u843d\u7684\u96ea\u82b1 \u632f\u596e\u7684\u5c0f\u8c93\u000d\u000a1.\u5728\u756b\u5177\u8c61\u756b\u6642\uff0c\u53ef\u4ee5\u5229\u7528\u4e00\u5f35\u767d\u7d19\u7528\u7b46\u000d\u000a\u756b\u51fa\u60f3\u8981\u7684\u5f62\u8c61\u3002\u000d\u000a2.\u4e4b\u5f8c\uff0c\u628a\u5b83\u526a\u4e0b\u3002\u9019\u6642\uff0c\u767d\u7d19\u4e0a\u6709\u4e86\u4e00\u000d\u000a\u500b\u5f62\u3002\u6a21\u677f\u5c31\u505a\u597d\u4e86\uff01\u000d\u000a\u6a21\u677f\uff1a\u6307\u526a\u51fa\u5f62\u72c0\uff0c\u8b93\u7c89\u5f69\u3001\u984f\u6599\u53ea\u7559\u5728\u7279\u000d\u000a\u5b9a\u5340\u57df\uff0c\u5f62\u6210\u5716\u6848\u3002\u000d\u000a\u5c0f\u6a21\u677f\u7684\u505a\u6cd5\u000d\u000a11","\u5b8c\u6210\u5f8c\u8f15\u5674\u5b9a\u756b\u6db2\uff08Fixative\uff09\uff0c\u4fdd\u000d\u000a\u6301\u8ddd\u96e2\u4e26\u8f15\u5674\u591a\u5c64\uff0c\u4ee5\u6e1b\u5c11\u984f\u6599\u6389\u843d\u3002\u000d\u000a\u5b58\u653e\u6642\u53ef\u5728\u756b\u4f5c\u9593\u593e\u4e00\u5f35\u73bb\u7483\u7d19\u6216\u9632\u000d\u000a\u9178\u7d19\uff0c\u4e26\u907f\u514d\u76f4\u63a5\u63a5\u89f8\u73bb\u7483\u6846\uff0c\u4ee5\u9632\u984f\u000d\u000a\u6599\u88ab\u58d3\u6389\u3002\u000d\u000a\u756b\u756b\u7684\u5c0f\u6487\u6b65\u000d\u000a\u756b \u597d \u80cc \u666f \u8272 \u5f8c \uff0c \u53ef \u4ee5 \u5148 \u5674 \u4e0a \u5b9a \u756b \u6db2\u000d\u000a\uff08Fixative\uff09\uff0c\u518d\u7e7c\u7e8c\u4f5c\u756b\u3002\u4f46\u662f\uff0c\u6b64\u6280\u000d\u000a\u6cd5\u7684\u80cc\u666f\u8272\u61c9\u8a72\u4fdd\u6301\u6dfa\u8272\uff0c\u9019\u6a23\u5f8c\u7e8c\u4e0a\u8272\u000d\u000a\u6642\u624d\u80fd\u66f4\u5bb9\u6613\u5448\u73fe\u7d30\u7bc0\u3002\u000d\u000a\u4e4b\u5f8c\u518d\u4ee5\u6df1\u8272\u81f3\u6dfa\u8272\u756b\uff0c\u907f\u000d\u000a\u514d\u4e00\u6b21\u5857\u592a\u539a\u5f71\u97ff\u5f8c\u7e8c\u758a\u000d\u000a\u8272\u3002\u000d\u000a\u624b\u6307\u80fd\u5275\u9020\u81ea\u7136\u67d4\u548c\u7684\u6f38\u8b8a\uff0c\u000d\u000a\u68c9\u68d2\u80fd\u66f4\u7cbe\u78ba\u5730\u63a7\u5236\u7d30\u7bc0\u3002\u000d\u000a\u53ef\u4ee5\u7528\u8edf\u76ae\u64e6\u8f15\u8f15\u64e6\u53bb\u90e8\u5206\u000d\u000a\u984f\u6599\u4f86\u88fd\u9020\u9ad8\u5149\uff0c\u6216\u5728\u6700\u5f8c\u000d\u000a\u7528\u767d\u8272\u7c89\u5f69\u52a0\u5f37\u4eae\u90e8\u3002\u000d\u000a12","\u5728\u5bb6\u4e2d\uff0c\u8981\u5982\u4f55\u53bb\u611f\u53d7\u7576\u4e0b\u60c5\u7dd2\uff0c\u4e26\u60f3\u51fa\u5176\u8c61\u5fb5\u7684\u984f\u000d\u000a\u8272\u5462\uff1f\u000d\u000a\u9996\u5148\uff0c\u5148\u9589\u773c\u300c\u611f\u53d7\u300d\u60c5\u7dd2\u672c\u8eab\u000d\u000a\u554f\u81ea\u5df1\uff1a\u9762\u5c0d\u4e00\u500b\u60c5\u5883\uff0c\u4f60\u7684\u60c5\u7dd2\u662f\u5982\u4f55\u7684\uff1f\u000d\u000a\u5617\u8a66\u7528\u4e94\u611f\u4f86\u60f3\u50cf\uff1a\u5982\u679c\u9019\u500b\u60c5\u7dd2\u6709\u6eab\u5ea6\u3001\u5473\u9053\u3001\u000d\u000a\u89f8\u611f\u3001\u8072\u97f3\uff0c\u5b83\u6703\u662f\u4ec0\u9ebc\uff1f\u9019\u80fd\u5e6b\u52a9\u4f60\u66f4\u5177\u9ad4\u6293\u4f4f\u000d\u000a\u611f\u89ba\u3002\u000d\u000a\u5176\u6b21\uff0c\u628a\u60c5\u7dd2\u806f\u60f3\u6210\u984f\u8272\u548c\u8272\u6eab\uff0c\u4f8b\u5982\uff1a\u000d\u000a\u6eab\u6696\u7684\u60c5\u7dd2\uff08\u559c\u6085\u3001\u611b\u3001\u632f\u596e\uff09 \uff1a\u5e38\u806f\u60f3\u5230\u7d05\u000d\u000a\u8272\u3001\u6a58\u8272\u3001\u9ec3\u8272\u3001\u5ae9\u7da0\u8272\u3002\u000d\u000a\u51b7\u51bd\u7684\u60c5\u7dd2\uff08\u60b2\u50b7\u3001\u5b64\u55ae\u3001\u6cae\u55aa\uff09 \uff1a\u5e38\u7528\u85cd\u8272\u3001\u000d\u000a\u7070\u8272\u3001\u7d2b\u8272\u3001\u58a8\u7da0\u8272\u3002\u000d\u000a\u5e73\u975c\u3001\u7642\u7652\u7684\u60c5\u7dd2\uff1a\u9752\u8272\u3001\u6dfa\u85cd\u8272\u3001\u7c73\u767d\u8272\u3001\u6de1\u7da0\u000d\u000a\u8272\u3002\u000d\u000a\u6fc0\u70c8\u3001\u885d\u7a81\u7684\u60c5\u7dd2\uff1a\u5f37\u70c8\u7684\u5c0d\u6bd4\u8272\uff0c\u5982\u7d05\u914d\u9ed1\u3001\u7d2b\u000d\u000a\u914d\u9ec3\u3002\u000d\u000a\u6700\u5f8c\uff0c\u4f7f\u7528\u984f\u8272\u300c\u6df1\u6dfa\u300d\u548c\u300c\u6df7\u8272\u300d\u4f86\u7d30\u81a9\u8868\u9054\uff0c\u4f8b\u000d\u000a\u5982\uff1a\u000d\u000a\u6dfa\u8272\uff1a\u67d4\u548c\u3001\u8f15\u76c8\u3001\u6b63\u9762\u3002\u000d\u000a\u6df1\u8272\uff1a\u539a\u91cd\u3001\u58d3\u8feb\u3001\u5167\u6582\u3002\u000d\u000a\u6f38\u5c64\u6df7\u8272\uff1a\u8868\u73fe\u60c5\u7dd2\u7684\u6d41\u52d5\u611f\uff0c\u4f8b\u5982\u5f9e\u60b2\u50b7\u6162\u6162\u8d70\u5411\u000d\u000a\u5e0c\u671b\u3002\u000d\u000a\u5982\u4f55\u7528\u8272\u5f69\u7e6a\u51fa\u60c5\u7dd2\uff1f\u000d\u000a13","\u5728\u7c89\u5f69\u756b\u7684\u5275\u4f5c\u4e2d\uff0c\u6211\u610f\u8b58\u5230\uff0c\u8272\u5f69\u5176\u5be6\u662f\u60c5\u7dd2\u7684\u53e6\u4e00\u7a2e\u8a9e\u8a00\u3002\u5feb\u6a02\u000d\u000a\u6642\uff0c\u6703\u81ea\u7136\u9078\u64c7\u660e\u4eae\u3001\u8df3\u8e8d\u7684\u8272\u5f69\uff1b\u800c\u5728\u5fc3\u60c5\u4f4e\u843d\u6216\u58d3\u529b\u8f03\u5927\u7684\u6642\u000d\u000a\u5019\uff0c\u6df1\u6c89\u7684\u8272\u8abf\u5247\u6703\u6084\u7136\u4f54\u64da\u756b\u9762\u3002\u9019\u6a23\u7684\u89ba\u5bdf\uff0c\u8b93\u6211\u5b78\u6703\u4e86\u4e0d\u8a55\u50f9\u000d\u000a\u81ea\u5df1\u7684\u60c5\u7dd2\uff0c\u800c\u662f\u900f\u904e\u7e6a\u756b\u53bb\u611f\u53d7\u3001\u91cb\u653e\u8207\u63a5\u7d0d\u3002\u6bcf\u4e00\u5f35\u756b\u4f5c\uff0c\u90fd\u662f\u000d\u000a\u4e00\u6b21\u81ea\u6211\u7642\u7652\u7684\u65c5\u7a0b\uff0c\u4e5f\u662f\u60c5\u7dd2\u8f49\u5316\u6210\u7f8e\u9e97\u98a8\u666f\u7684\u898b\u8b49\u3002\u000d\u000a\u65c5\u9014\u5713\u6eff\uff01\u000d\u000a\u300c\u7d50\u675f\u4e5f\u662f\u65b0\u7684\u958b\u59cb\u300d"]